import React, { useState } from 'react';
import { Header } from '../common/Header';

// Import the Search component
import Search from './Search';

import '../styles/recipes.css';
import '../styles/specialRecipes.css';

export const Recipes = () => {
  const [recipeList, setRecipeList] = useState([]);
  const [recipeModal, setRecipeModal] = useState([]);

  // Function to fetch recipe data from the backend
  const getRecipeList = (ingredients) => {
    fetch(`http://localhost:3000/recipes/?ingredients=${ingredients}`)
      .then((response) => response.json())
      .then((data) => {
        if (data && data.recipes) {
          setRecipeList(data.recipes);
        }
      })
      .catch((error) => {
        console.error('Error fetching recipe data:', error);
      });
  };

  // Other functions for showModal and hideModal...

  return (
    <>
      <header class="header">
        <Header />
        <div class="container">
          <div class="meal-wrapper">
            <div class="meal-search">
              <h2 class="title">Pop in your ingredients and find a tasty meal!</h2>
              <blockquote>Real food doesn't have ingredients = real food is ingredients.</blockquote>

              {/* Render the Search component */}
              <Search onSearch={getRecipeList} />

            </div>

            {/* Rest of the Recipes component... */}
            {/* Including the recipeList mapping and modal code */}
          </div>
        </div>
      </header>
    </>
  );
};
