import React, { useState } from "react";
import { Header } from "../common/Header";
import "../styles/recipes.css";
import "../styles/specialRecipes.css";

export const Recipes = () => {
  const [recipeList, setRecipeList] = useState([]);
  const [recipeModal, setRecipeModal] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [showPopup, setShowPopup] = useState(false);

  function getRecipeList() {
    fetch(`http://localhost:3000/recipes/?ingredients=${searchQuery}`)
      .then((response) => response.json())
      .then((data) => {
        setRecipeList(data);
      })
      .catch((error) => {
        console.error("Error fetching recipes:", error);
        // Handle the error here, such as displaying an error message to the user
      });
  }

  function showModal(e) {
    e.preventDefault();

    let mealItem = e.target.parentElement.parentElement;
    const recipeId = mealItem.dataset.id;

    fetch(`http://localhost:3000/recipes/${recipeId}`)
      .then((response) => response.json())
      .then((data) => setRecipeModal(data))
      .catch((error) => {
        console.error("Error fetching recipe details:", error);
        // Handle the error here, such as displaying an error message to the user
      });

    const mealDetailsContent = document.querySelector(".meal-details-content");
    mealDetailsContent.parentElement.classList.add("showRecipe");
  }

  function hideModal() {
    const mealDetailsContent = document.querySelector(".meal-details-content");
    mealDetailsContent.parentElement.classList.remove("showRecipe");
    setRecipeModal([]);
  }

  function handleSearchClick() {
    setShowPopup(true);
  }

  function handlePopupSearch() {
    setShowPopup(false);
    getRecipeList();
  }

  return (
    <>
      <header className="header">
        <Header />
        <div className="container">
          <div className="meal-wrapper">
            <div className="meal-search">
              <h2 className="title">Pop in your ingredients and find a tasty meal!</h2>
              <blockquote>Real food doesn't have ingredients = real food is ingredients.<br/></blockquote>
              <div className="meal-search-box">
                <div className="input-box">
                  <i className="uil uil-search"></i>
                  <input type="text" className="searchRecipe" placeholder="Search here..." value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} />
                  <button className="button" onClick={handleSearchClick}>Search</button>
                </div>
              </div>
            </div>

            <div className="meal-result">
              <h2 className="title">Your Search Results:</h2>
              <div id="meal">
                {recipeList.map((meal) => (
                  <div className="meal-item" data-id={meal.idMeal} key={meal.idMeal}>
                    <h4>{meal.strMeal}</h4>
                    <div className="rating">
                      <span><i style={{ color: "orange" }} className="bx bxs-star"></i></span>
                      <span><i style={{ color: "orange" }} className="bx bxs-star"></i></span>
                      <span><i style={{ color: "orange" }} className="bx bxs-star"></i></span>
                      <span><i style={{ color: "orange" }} className="bx bxs-star"></i></span>
                      <span><i style={{ color: "orange" }} className="bx bxs-star"></i></span>
                    </div>
                    <div onClick={(e) => showModal(e)} className="button btn" style={{ marginTop: "20px", marginBottom: "20px" }}>View Recipe</div>
                  </div>
                ))}
              </div>
            </div>

            {showPopup && (
              <div className="meal-wrapper">
                <div className="meal-details">
                  <button type="button" className="btn recipe-close-btn" id="recipe-close-btn" onClick={() => setShowPopup(false)}>x</button>
                  <div className="meal-details-content">
                    <h2 className="recipe-title">Search Popup</h2>
                    <div className="recipe-instruct">
                      <h3>Enter Ingredients:</h3>
                      <input type="text" className="searchRecipePopup" placeholder="Enter ingredients..." value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} />
                      <button className="button" onClick={handlePopupSearch}>Search</button>
                    </div>
                  </div>
                </div>
              </div>
            )}

            <div className="meal-wrapper">
              <div className="meal-details">
                <button type="button" className="btn recipe-close-btn" id="recipe-close-btn" onClick={() => hideModal()}>x</button>
                <div className="meal-details-content">
                  <h2 className="recipe-title">{recipeModal.strMeal}</h2>
                  <p className="recipe-category">{recipeModal.strCategory}</p>
                  <div className="recipe-instruct">
                    <h3>Instructions:</h3>
                    <p>{recipeModal.strInstructions}</p>
                  </div>
                  <div className="recipe-link">
                    <a style={{ fontSize: "2rem", textDecoration: "underline" }} href={recipeModal.strYoutube}>Watch Video</a>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </header>
    </>
  );
};
